import { default as Gesto, OnDrag, OnDragStart } from 'gesto';
import { default as EventEmitter } from '@scena/event-emitter';

export default class ScrollBar extends EventEmitter {
    type: "horizontal" | "vertical";
    isAppend: boolean;
    thumbElement: HTMLElement;
    barElement: HTMLElement;
    protected gesto: Gesto;
    protected size: number;
    protected scrollSize: number;
    protected isHorizontal: boolean;
    constructor(containerElement: HTMLElement, type: "horizontal" | "vertical", container?: HTMLElement);
    scrollBy(delta: number, isAbsolute?: boolean): void;
    render(isDisplay: boolean, pos: number, size: number, scrollSize: number): void;
    destroy(): void;
    protected _onDragStart: (e: OnDragStart<Gesto>) => void;
    protected _onDrag: (e: OnDrag) => void;
    protected _onWheel: (e: WheelEvent) => void;
}
